catalog_script_client.script:
function onLoad() {
   //Type appropriate comment here, and begin script below
	var search_params = getUrlParams(top.location.href);
	var parent_id = search_params['parent_id'];
	// if this is not cloning, don't need to do anything
	if(!parent_id) {
		return;
	}
	try {
		var ga = new GlideAjax("global.DominoUtils"); 
		ga.addParam("sysparm_name","retrieveFwRequest");
		ga.addParam("sys_id", parent_id);
		ga.getXML(getResponse);
	}
	catch(err) {
		alert("Domino app got an error: " + err.message);
	}

	function getResponse(response) {
		var answer = response.responseXML.documentElement.getAttribute("answer");
		result = JSON.parse(answer);
		g_form.setValue("u_project_name", result["u_project_name"]);
		g_form.setValue("u_service_wbc_code", result["u_service_wbc_code"]);
		g_form.setValue("u_service_name", result["u_service_name"]);
		g_form.setValue("u_service_manager", result["u_service_manager"]);
		g_form.setValue("u_requested_for", result["u_requested_for"]);
		g_form.setValue("u_project_name", result["u_project_name"]);
		g_form.setValue("u_options", result["u_options"]);
		g_form.setValue("u_deputies", result["u_deputies"]);
		g_form.setValue("u_comment", result["u_comment"]);
		g_form.setValue("server_groups", result["server_groups"]);
		g_form.setValue("requested_expiration_date", result["requested_expiration_date"]);
		g_form.setValue("network_groups", result["network_groups"]);
		g_form.setValue("estimated_start_date", result["estimated_start_date"]);
		g_form.setValue("description", result["description"]);
		g_form.setValue("domino_firewall_rules_list", result["rules"]);
		g_form.setValue("domino_parent", parent_id);
		g_form.setValue("being_cloned", "true");
	}
	function getUrlParams(url) {
		// Note: This function does not consider url hash
		var search = url.split('?', 2)[1];
		var keyvals = search.split('&');
		var params = {};
		for (var i =0; i < keyvals.length; i++) {
			var pair = keyvals[i].split('=');
			params[pair[0]] = decodeURIComponent(pair[1]);
		}				
		return params;
	}
}
