sp_widget.css:
.nav {
  li {
    padding: 10px 7px;

    > span {
      > a {
          display: inline;
      }

      > i {
          padding-left: 15px;
      }
    }
  }
}

.a-disabled {
  cursor: default;
  color: $sp-disabled-breadcrumb;
}

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */

	data.breadcrumbs = [];
	//data.color = options.color;
	var label;
	var labels;
	var page_id;
	var page_ids;

	var page = $sp.getParameter('id');
	label = options.label||page;
	page_id = options.page_id||page;

	labels = label.split(',');
	page_ids = page_id.split(',');

	for(var i=0; i<labels.length && i<page_ids.length; i++){

		data.breadcrumbs.push({label:labels[i], url:"?id=" + page_ids[i]});
		gs.info('-->labels =' + data.breadcrumbs[i]);
	}
})();
