sp_widget.client_script:
function($scope, $rootScope, spUtil) {
	var c = this;
	c.expanded = !spUtil.isMobile();
	c.expand = function() {
		c.expanded = true;
	};

	var deregister = $rootScope.$on("sp.update.breadcrumbs", function(e, list) {
		c.data.breadcrumbs = list;
	});

	$scope.$on('$destroy', function(){
		deregister();
	});

}

sp_widget.css:
.nav {
  li {
    padding: 10px 7px;

    > span {
      > a {
          display: inline;
      }

      > i {
          padding-left: 15px;
      }
    }
  }
}

.a-disabled {
  cursor: default;
  color: $sp-disabled-breadcrumb;
}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

	var t = data;
	t.page_id = $sp.getParameter('id');
    gs.info('--->page id :' + t.page_id);



 t.breadcrumbs = [{label: "My Firewall Requests", url: 'domino?id=' + t.page_id}]; 
t.breadcrumbs.unshift({label: "Firewall Request", url: 'domino?id=domino_landing'});

gs.info('--->breadcrumbs object =' + t.breadcrumbs[0].label + " " + t.breadcrumbs[1].label );

gs.info('--->breadcrumbs URL =' + t.breadcrumbs[0].url + " " + t.breadcrumbs[1].url );

})();
