sys_relationship.query_with:
(function refineQuery(current, parent) {

	// Add your code here, such as current.addQuery(field, value);
	var ids = [];
	var rules = new GlideRecord('x_513186_domino_ap_firewall_rules');
// 	rules.addQuery('source_server_network_group', parent.sys_id);

	var bal = rules.addQuery('source_server_network_group', parent.sys_id);
	bal.addOrCondition('dest_server_network_group', parent.sys_id );

	rules.query();
	while (rules.next()) {
		ids.push(rules.firewall_req_id.toString());

		gs.info('--> Firewall Req id' + rules.firewall_req_id);
	}
// 	current.addQuery("firewall_req_id", 'IN', ids);
	current.addQuery("sys_id", 'IN', ids);

})(current, parent);
