sys_relationship.query_with:
(function refineQuery(current, parent) {

	// Add your code here, such as current.addQuery(field, value);
	var ids = [];
	var rules = new GlideRecord('x_513186_domino_ap_firewall_rules');
	rules.addQuery('firewall_req_id', parent.sys_id);
	rules.query();
	while (rules.next()) {
		ids.push(rules.dest_server_network_group);
		ids.push(rules.source_server_network_group);
	}
	current.addQuery("sys_id", 'IN', ids);
})(current, parent);
