sys_script_include.script:
var DominoFilterUtils = Class.create();
DominoFilterUtils.prototype = {
	initialize: function() {
	},
	getSvrGroupForSimpleList: function() {
		var list = [];
		var gr = new GlideRecord("x_513186_domino_ap_fw_server_network_groups");
		gr.query();
		while(gr.next()) {
			var is_active = this.checkActive(gr.sys_id);
			if(is_active == 0) {
				continue;
			}
			list.push(gr.sys_id.toString());
		}
		return list;		
	},
	getSvrGroup: function() {
		var list = '';
		var gr = new GlideRecord("x_513186_domino_ap_fw_server_network_groups");
		gr.query();
		while(gr.next()) {
			var is_active = this.checkActive(gr.sys_id);
			if(is_active == 0) {
				continue;
			}
			if (list.length > 0) {
				//build a comma separated string of groups if there is more than one
				list += (',' + gr.sys_id);
			}
			else {

				list = String(gr.sys_id);
			}
		}
		return 'sys_idIN' + list;		
	},
	checkActive: function(sys_id){
		var rule = new GlideRecord('x_513186_domino_ap_firewall_rules');
		//rule.addQuery('source_server_network_group', sys_id);
		var query = 'source_server_network_group=' + sys_id + '^ORdest_server_network_group=' + sys_id;
		rule.addEncodedQuery(query);
		rule.query();
		// active if no rule uses this group yet
		if(rule.getRowCount() == 0)
			return 1;
		while(rule.next()) {
			var fw = new GlideRecord('x_513186_domino_ap_firewall');
			fw.get(rule.firewall_req_id);
			if(fw) {
				if(fw.state != 3)
					return 0;
			}
		}
		return 1;
	},	
	type: 'DominoFilterUtils'
};
